mapper.insert:

insert into lecture (lec_number, lec_room, title, 
  speaker, lec_score, lec_date, 
  lec_time, introduction, content
  )
values (#{lecNumber,jdbcType=VARCHAR}, #{lecRoom,jdbcType=VARCHAR}, #{title,jdbcType=VARCHAR}, 
  #{speaker,jdbcType=VARCHAR}, #{lecScore,jdbcType=DOUBLE}, #{lecDate,jdbcType=VARCHAR},
  #{lecTime,jdbcType=VARCHAR}, #{introduction,jdbcType=LONGVARCHAR}, #{content,jdbcType=LONGVARCHAR}
  )

mapper.update:
update lecture set lec_number = #{lecNumber},lec_room = #{lecRoom},title = #{title},
speaker = #{speaker},introduction = #{introduction},lec_score = #{lecScore},lec_date = #{lecDate},
lec_time = #{lecTime},content = #{content} where lec_number  = #{lecNumber}
